package srp

import (
  "errors"
  "hash"
  "math/big"
  "github.com/metebalci/gfa"
)

var bigZero = big.NewInt(0)

const (
  rfc5054_1024_N = "EEAF0AB9ADB38DD69C33F80AFA8FC5E86072618775FF3C0B9EA2314C9C256576D674DF7496EA81D3383B4813D692C6E0E0D5D8E250B98BE48E495C1D6089DAD15DC7D7B46154D6B6CE8EF4AD69B15D4982559B297BCF1885C529F566660E57EC68EDBC3C05726CC02FD4CBF4976EAA9AFD5138FE8376435B9FC61D2FC0EB06E3"
  rfc5054_1024_g = "2"
  rfc5054_1536_N = ""
  rfc5054_1536_g = "2"
  rfc5054_2048_N = ""
  rfc5054_2048_g = "2"
  rfc5054_3072_N = ""
  rfc5054_3072_g = "5"
  rfc5054_4096_N = ""
  rfc5054_4096_g = "5"
  rfc5054_6144_N = ""
  rfc5054_6144_g = "5"
  rfc5054_8192_N = ""
  rfc5054_8192_g = "13" // this is given as 19 (decimal) in the RFC
)

type Srp struct {
  N *big.Int
  g *big.Int
  k *big.Int
  Nbytes []byte
  big0 *big.Int
  hashN_xor_hashg_bytes []byte
  H hash.Hash 
  sch SrpClientHelper
  ssh SrpServerHelper
}

type SrpClientHelper interface {
  srp_a() []byte
}

type SrpServerHelper interface {
  srp_b() []byte
  srp_s(string) []byte
  srp_v(string) []byte
}

type SrpProtocol interface {
  hash_reset()
  hash_add(a *big.Int)
  hash_add_with_pad(a *big.Int)
  hash_add_bytes(a []byte)
  hash_add_bytes_with_pad(a []byte)
  hash_sum() *big.Int
  hash_sum_bytes() []byte
  srp_A(a *big.Int) *big.Int
  srp_B(k *big.Int, v *big.Int, b *big.Int) *big.Int
  srp_u(A *big.Int, B *big.Int) *big.Int
  srp_x(s []byte, p *big.Int) *big.Int
  srp_S_user(B *big.Int, k *big.Int, g *big.Int, x *big.Int, a *big.Int, u *big.Int) *big.Int
  srp_S_host(A *big.Int, v *big.Int, u *big.Int, b *big.Int) *big.Int
  srp_K_user(S *big.Int) *big.Int
  srp_K_host(S *big.Int) *big.Int
  srp_M() *big.Int
  srp_HAMK(A *big.Int, M *big.Int, K *big.Int) *big.Int
  SrpXchg1_User() (*big.Int, *big.Int, error)
  SrpXchg2_Host(I string, A *big.Int) ([]byte, *big.Int, *big.Int, *big.Int, *big.Int, error)
  SrpXchg3_User(I string, p string, s []byte, A, B, a *big.Int) (*big.Int, *big.Int, *big.Int, error)
  SrpXchg4_Host(I string, s []byte, A, B, K, M *big.Int) (*big.Int, error)
  SrpXchg5_User(A, K, M, HAMK *big.Int) error
}

func NewSrp(N, g, k *big.Int, H hash.Hash, sch SrpClientHelper, ssh SrpServerHelper) *Srp {
  srp := new(Srp)
  srp.N = N
  srp.g = g
  srp.H = H
  srp.sch = sch
  srp.ssh = ssh
  srp.big0 = big.NewInt(0)
  srp.Nbytes = int2bytes(N)
  srp.H = H
  if k == nil {
    srp.hash_reset()
    srp.hash_add(N)
    srp.hash_add_with_pad(g)
    srp.k = srp.hash_sum()
  } else {
    srp.k = k
  }
  srp.hash_reset()
  srp.hash_add_bytes(srp.Nbytes)
  hashN := srp.hash_sum_bytes()
  srp.hash_reset()
  srp.hash_add(srp.g)
  hashg := srp.hash_sum_bytes()
  for i := 0; i < len(hashN); i++ {
    hashN[i] = hashN[i]  ^ hashg[i]
  }
  srp.hashN_xor_hashg_bytes = hashN
  return srp
}

func (srp *Srp) hash_reset() {
  srp.H.Reset()
}

func (srp *Srp) hash_add(a *big.Int) {
  b := int2bytes(a)
  srp.H.Write(b)
}

func (srp *Srp) hash_add_with_pad(a *big.Int) {
  b := int2bytes(a)
  pad_size := len(srp.Nbytes) - len(b)
  srp.H.Write(make([]byte, 0, pad_size))
  srp.H.Write(b)
}

func (srp *Srp) hash_add_bytes(a []byte) {
  srp.H.Write(a)
}

func (srp *Srp) hash_add_bytes_with_pad(a []byte) {
  pad_size := len(srp.Nbytes) - len(a)
  srp.H.Write(make([]byte, 0, pad_size))
  srp.H.Write(a)
}


func (srp *Srp) hash_sum() *big.Int {
  return bytes2int(srp.H.Sum(nil))
}

func (srp *Srp) hash_sum_bytes() []byte {
  return srp.H.Sum(nil)
}

func int2bytes(a *big.Int) []byte {
  return a.Bytes()
}

func bytes2int(a []byte) *big.Int {
  return new(big.Int).SetBytes(a)
}

func (srp *Srp) srp_A(a *big.Int) *big.Int {
  gf := gfa.New(srp.N)
  return gf.Exp(srp.g, a)
}

func (srp *Srp) srp_B(k, v, b *big.Int) *big.Int {
  gf := gfa.New(srp.N)
  return gf.Add(gf.Mul(k, v), gf.Exp(srp.g, b))
}

func (srp *Srp) srp_u(A, B *big.Int) *big.Int {
  srp.hash_reset()
  srp.hash_add_with_pad(A)
  srp.hash_add_with_pad(B)
  return srp.hash_sum()
}

func (srp *Srp) srp_x(I string, s []byte, p string) *big.Int {
  srp.hash_reset()
  srp.hash_add_bytes([]byte(I))
  srp.hash_add_bytes([]byte(":"))
  srp.hash_add_bytes([]byte(p))
  tmp := srp.hash_sum_bytes()
  srp.hash_reset()
  srp.hash_add_bytes(s)
  srp.hash_add_bytes(tmp)
  return srp.hash_sum()
}

func (srp *Srp) srp_S_user(B, k, x, a, u *big.Int) *big.Int {
  gf := gfa.New(srp.N)
  return gf.Exp(gf.Sub(B, gf.Mul(k, gf.Exp(srp.g, x))), gf.Add(a, gf.Mul(u, x)))
}

func (srp *Srp) srp_S_host(A, v, u, b *big.Int) *big.Int {
  gf := gfa.New(srp.N)
  return gf.Exp(gf.Mul(A, gf.Exp(v, u)), b)
}

func (srp *Srp) srp_K_user(S *big.Int) *big.Int {
  srp.hash_reset()
  srp.hash_add(S)
  return srp.hash_sum()
}

func (srp *Srp) srp_K_host(S *big.Int) *big.Int {
  srp.hash_reset()
  srp.hash_add(S)
  return srp.hash_sum()
}

func (srp *Srp) srp_M(I string, s []byte, A, B, K *big.Int) *big.Int {
  srp.hash_reset()
  srp.hash_add_bytes([]byte(I))
  hashI := srp.hash_sum_bytes()
  srp.hash_reset()
  srp.hash_add_bytes(srp.hashN_xor_hashg_bytes)
  srp.hash_add_bytes(hashI)
  srp.hash_add_bytes(s)
  srp.hash_add(A)
  srp.hash_add(B)
  srp.hash_add(K)
  return srp.hash_sum()
}

func (srp *Srp) srp_HAMK(A, M, K *big.Int) *big.Int {
  srp.hash_reset()
  srp.hash_add(A)
  srp.hash_add(M)
  srp.hash_add(K)
  return srp.hash_sum()
}

func (srp *Srp) SrpXchg1_User() (*big.Int, *big.Int, error) {
  aBytes := srp.sch.srp_a()
  if len(aBytes) < 16 {
    return nil, nil, errors.New("insufficient security, len(a) < 16")
  }
  a := bytes2int(aBytes)
  A := srp.srp_A(a)
  return a, A, nil
}

func (srp *Srp) SrpXchg2_Host(I string, A *big.Int) ([]byte, *big.Int, *big.Int, *big.Int, *big.Int, error) {
  if A.Cmp(srp.big0) == 0 {
    return nil, nil, nil, nil, nil, errors.New("insufficient security, A is zero!")
  }
  s := srp.ssh.srp_s(I)
  v := bytes2int(srp.ssh.srp_v(I))
  bBytes := srp.ssh.srp_b()
  if len(bBytes) < 16 {
    return nil, nil, nil, nil, nil, errors.New("illegal parameter, len(b) < 16")
  }
  b := bytes2int(bBytes)
  k := srp.k
  B := srp.srp_B(k, v, b)
  u := srp.srp_u(A, B)
  S := srp.srp_S_host(A, v, u, b)
  K := srp.srp_K_host(S)
  return s, B, u, S, K, nil
}

func (srp *Srp) SrpXchg3_User(I string, p string, s []byte, A, B, a *big.Int) (*big.Int, *big.Int, *big.Int, error) {
  if B.Cmp(srp.big0) == 0 {
    return nil, nil, nil, errors.New("illegal parameter, B is zero!")
  }
  u := srp.srp_u(A, B)
  if u.Cmp(srp.big0) == 0 {
    return nil, nil, nil, errors.New("illegal parameter, u is zero!")
  }
  x := srp.srp_x(I, s, p)
  S := srp.srp_S_user(B, srp.k, x, a, u)
  K := srp.srp_K_user(S)
  M := srp.srp_M(I, s, A, B, K)
  return S, K, M, nil
}

func (srp *Srp) SrpXchg4_Host(I string, s []byte, A, B, K, user_M *big.Int) (*big.Int, error) {
  computed_M := srp.srp_M(I, s, A, B, K)
  if computed_M.Cmp(user_M) != 0 {
    return nil, errors.New("bad_record_mac, M")
  }
  return srp.srp_HAMK(A, user_M, K), nil
}

func (srp *Srp) SrpXchg5_User(A, K, M, host_HAMK *big.Int) error {
  computed_HAMK := srp.srp_HAMK(A, M, K)
  if computed_HAMK.Cmp(host_HAMK) != 0 {
    return errors.New("mismatch, H(A|M|K)")
  } else {
    return nil
  }
}
